import React, { createContext, useState, useEffect } from "react";
import { UserContext } from "../User/UserProvider";

export const CartContext = createContext();

function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);

  // Load cart items from localStorage
  useEffect(() => {
    const storedCartItems = localStorage.getItem("cartItems");
    if (storedCartItems) {
      setCartItems(JSON.parse(storedCartItems, cartItemReviver));
    }
  }, []);

  // Use localStorage to persist cart items
  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems, cartItemReplacer));
  }, [cartItems]);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeFromCart = (itemId) => {
    const updatedCartItems = cartItems.filter((item) => item.id !== itemId);
    setCartItems(updatedCartItems);
  };

  const clearCart = () => {
    setCartItems([]);
  };

  const getCartSize = () => {
    return cartItems.length;
  };

  const calculateTotalAmount = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  // Custom replacer function for serializing CartItem objects
  const cartItemReplacer = (key, value) => {
    if (value instanceof CartItem) {
      return { ...value }; // Convert CartItem to a plain JavaScript object
    }
    return value;
  };

  // Custom reviver function for deserializing CartItem objects
  const cartItemReviver = (key, value) => {
    if (typeof value === "object" && value !== null && "id" in value && "price" in value) {
      return new CartItem(value.id, value.price);
    }
    return value;
  };

  // Example CartItem class
  class CartItem {
    constructor(id, price) {
      this.id = id;
      this.price = price;
    }
  }

  return (
    <CartContext.Provider
      value={{
        cartItems,
        addToCart,
        removeFromCart,
        clearCart,
        getCartSize,
        calculateTotalAmount,
      }}
    >
      {children}
    </CartContext.Provider>
  );
}

export default CartProvider;
