import React, { useState } from 'react';
import EastOutlinedIcon from '@mui/icons-material/EastOutlined';
import WestOutlinedIcon from '@mui/icons-material/WestOutlined';
import './Slider.css';

const Slider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const images = [
    'https://www.foodandwine.com/thmb/VOCShOG5uYdtSsDtT88m-M8WYt0=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/history-of-american-candy-bars-4-FT-BLOG1018-c717e0e2e09b4b509791cc08afc1ee26.jpg',
    'https://upload.wikimedia.org/wikipedia/commons/thumb/5/56/Candy-Corn.jpg/1200px-Candy-Corn.jpg',
    'https://cdn.shopify.com/s/files/1/2429/9455/products/sweet-spindles-mini-candy-sticks-rainbow-133392-am-im_1024x1024@2x.jpg?v=1539297101',
  ];

  const prevSlide = () => {
    setCurrentSlide(currentSlide === 0 ? images.length - 1 : currentSlide - 1);
  };

  const nextSlide = () => {
    setCurrentSlide(currentSlide === images.length - 1 ? 0 : currentSlide + 1);
  };

  return (
    <div className="slider">
      <div className="container" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
        {images.map((image, index) => (
          <img src={image} alt={`Slide ${index}`} key={index} className="img" />
        ))}
      </div>
    </div>
  );
};

export default Slider;
