import React from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { CardNumberElement, CardExpiryElement, CardCvcElement, Elements, useStripe, useElements } from '@stripe/react-stripe-js';
import CheckoutStyles from './CheckOut.js';
const stripePromise = loadStripe('pk_test_1234567890'); 

const CheckoutForm = () => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      return;
    }

    const { error } = await stripe.redirectToCheckout({
      lineItems: [
        // Add line items with details about the product being purchased
        { price: 'YOUR_PRODUCT_PRICE_ID', quantity: 1 },
      ],
      mode: 'payment',
      successUrl: 'https://your-website.com/success',
      cancelUrl: 'https://your-website.com/cancel',
      paymentMethodTypes: ['card'],
    });

    if (error) {
      console.error(error);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Secure Checkout</h2>
      <form onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Card Number:</label>
          <CardNumberElement
            options={{
              style: {
                base: {
                  fontSize: '16px',
                  width: '100%',
                  padding: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '3px',
                  letterSpacing: '0.5px',
                  fontFamily: 'Arial, sans-serif',
                },
              },
              placeholder: '1234 5678 9012 3456',
            }}
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>Expiry Date:</label>
          <CardExpiryElement
            options={{
              style: {
                base: {
                  fontSize: '16px',
                  width: '100%',
                  padding: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '3px',
                  letterSpacing: '0.5px',
                  fontFamily: 'Arial, sans-serif',
                },
              },
              placeholder: 'MM/YY',
            }}
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>CVC:</label>
          <CardCvcElement
            options={{
              style: {
                base: {
                  fontSize: '16px',
                  width: '100%',
                  padding: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '3px',
                  letterSpacing: '0.5px',
                  fontFamily: 'Arial, sans-serif',
                },
              },
              placeholder: '123',
            }}
          />
        </div>
        <button type="submit" style={styles.button}>
          Pay Now
        </button>
      </form>
    </div>
  );
};

const App = () => {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
};

export default App;
