//Products.jsx

  import React, { useEffect, useState } from "react";
  import { getAllProducts } from "../../../../server/productController.js";
  
  function ProductDisplay({ product }) {
    return (
      <div className="product">
        <h2>{product.name}</h2>
        <img src={product.image_address} alt={`${product.name} img`} />
        <p>Price: {product.price}</p>
        <button>Add to cart</button>
      </div>
    );
  }
  
  const Products = (productType) => {
    
    if (!(productType >= 1 && productType <= 3)){
        console.error('Invalid item type');
        return;
    }

    const [products, setProducts] = useState([]);
  
    useEffect(() => {
      getAllProducts()
        .then(products => {
          setProducts(products);
        })
        .catch(error => {
          console.error('Error retrieving products:', error);
        });
    }, []);
  
    const filteredProducts = products.filter(product => product.type === productType);
  
    return (
      <div className="allProducts">
        {filteredProducts.map(product => (
          <div key={product.id}><ProductDisplay product={product} /></div>
        ))}
      </div>
    );
  }
  
  export default Products;
