import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { CartContext } from "../Cart/CartProvider.jsx";
import { useLocation, useParams, useNavigate } from "react-router-dom";
import { UserContext } from "../User/UserProvider.jsx";
import CandyDisplay from "./CandyDisplay.jsx";

function Reviews() {
  const { productType, product } = useParams();
  const [reviews, setReviews] = useState([]);
  const [productObject, setProductObject] = useState({});
  const [comment, setComment] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const [submissionStatus, setSubmissionStatus] = useState(0);
  const { user } = useContext(UserContext);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProductAndReviews = async () => {
      try {
        const productResponse = await axios.get(
          `http://localhost:4000/api/products/${productType}/${product}`
        );
        setProductObject(productResponse.data);
        setLoading(false);
        console.log("productObject: ", productResponse.data);
      } catch (error) {
        console.error("Error retrieving product:", error);
        setLoading(false);
        setError(true);
      }

      try {
        const reviewsResponse = await axios.get(
          `http://localhost:4000/api/${productType}/${product}/reviews`
        );
        setReviews(reviewsResponse.data);
      } catch (error) {
        console.error("Error retrieving reviews:", error);
        setError(true);
      }
    };

    fetchProductAndReviews();
  }, [productType, product, location, submissionStatus]);

  console.log("reviews: ", reviews);

  const handleInputChange = (event) => {
    setComment(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    if (!user) {
      console.log("User not logged in");
      alert("Please log in to add a review");
      navigate("/account");
      return;
    }

    const authorEmail = user.email;

    try {
      const addSuccess = await axios.post(
        `http://localhost:4000/api/${productType}/${product}/reviews/add`,
        { authorEmail, comment }
      );

      if (addSuccess) {
        console.log("Added review");
        setSubmissionStatus((prevStatus) => prevStatus + 1); // Trigger re-render of parent components
        setComment(""); // Clear the input field
      } else {
        console.log("Failed to add review");
      }
    } catch (error) {
      console.error("Error adding review:", error);
    }
  };

  const ReviewDisplay = (props) => {
    return (
      <div className="review">
        <h2>{props.review.email}</h2>
        <p>{props.review.comment}</p>
      </div>
    );
  };

  if (loading) {
    return <h1>Loading...</h1>;
  }

  if (error) {
    return <h1>Error occurred while fetching data</h1>;
  }

  if (!productObject) {
    return null;
  }

  return (
    <div>
      <CandyDisplay product={productObject} />
      <div className="review-form">
        <h2>Write a Review</h2>
        <form onSubmit={handleSubmit}>
          <textarea
            name="comment"
            placeholder="Your Review"
            value={comment}
            onChange={handleInputChange}
            required
          ></textarea>
          <button type="submit">Submit Review</button>
        </form>
      </div>
      {!reviews || reviews.length === 0 ? (
        <div className="retrError">
          <h1>No reviews for this product</h1>
        </div>
      ) : (
        reviews.map((review) => <ReviewDisplay review={review} key={review.id} />)
      )}
    </div>
  );
}

export default Reviews;
