import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { CartContext } from "../Cart/CartProvider.jsx";
import { useLocation, useParams, useNavigate } from "react-router-dom";
import { UserContext } from "../User/UserProvider.jsx";
import CandyDisplay from "./CandyDisplay.jsx";

function Reviews() {
  const { productType, product } = useParams();
  const [reviewsAndObject, setReviewsAndObject] = useState({});
  const [comment, setComment] = useState("");
  const [error, setError] = useState(false);
  const [submissionStatus, setSubmissionStatus] = useState(0);
  const { user } = useContext(UserContext);
  const location = useLocation();
  const navigate = useNavigate();

  const handleInputChange = (event) => {
    setComment(event.target.value);
  };

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4000/api/${productType}/${product}/reviews`
        );
        setReviewsAndObject(response.data);
      } catch (error) {
        setError(true);
        console.error("Error fetching reviews:", error);
      }
    };

    fetchReviews();
  }, [productType, product, submissionStatus]);

  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    if (!user) {
      console.log("User not logged in");
      alert("Please log in to add a review");
      navigate("/account");
      return;
    }

    const authorEmail = user.email;

    try {
      const addSuccess = await axios.post(
        `http://localhost:4000/api/${productType}/${product}/reviews/add`,
        { authorEmail, comment }
      );

      if (addSuccess) {
        console.log("Added review");
        setSubmissionStatus((prevStatus) => prevStatus + 1); // Trigger re-render of parent components
        setComment(""); // Clear the input field
      } else {
        console.log("Failed to add review");
      }
    } catch (error) {
      console.error("Error adding review:", error);
    }
  };

  const ReviewDisplay = ({ review }) => {
    return (
      <div className="review" key={review._id}>
        <h2>{review.email}</h2>
        <p>{review.comment}</p>
      </div>
    );
  };

  if (error) {
    return <h1>Error occurred while fetching data</h1>;
  }

  console.log(reviewsAndObject === null)

  console.log("reviewsAndObject:", reviewsAndObject);

  return (
    <div>
      <CandyDisplay product={reviewsAndObject} />
      <div className="review-form">
        <h2>Write a Review</h2>
        <form onSubmit={handleSubmit}>
          <textarea
            name="comment"
            placeholder="Your Review"
            value={comment}
            onChange={handleInputChange}
            required
          ></textarea>
          <button type="submit">Submit Review</button>
        </form>
      </div>
      {!reviewsAndObject.reviews || reviewsAndObject.reviews.length === 0 ? (
        <div className="retrError">
          <h1>No reviews for this product</h1>
        </div>
      ) : (
        reviewsAndObject.reviews.map((review) => (
          <ReviewDisplay key={review._id} review={review} />
        ))
      )}
    </div>
  );
}

export default Reviews;
