import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { CartContext } from "../Cart/CartProvider.jsx";
import { useLocation, useParams, Link } from "react-router-dom";
import CandyDisplay from "./CandyDisplay.jsx";
import { UserContext } from "../../pages/User/UserProvider.jsx";

function Reviews() {
  const { productType, product } = useParams();
  const [reviews, setReviews] = useState([]);
  const [userReview, setUserReview] = useState({ authorEmail: "", comment: "" });
  const [productObject, setProductObject] = useState({});
  const location = useLocation();
  const { userContext } = useContext(UserContext);

  useEffect(() => {
    const fetchProductObject = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4000/api/products/${productType}/${product}`
        );
        console.log("response.data: ", response.data);
        setProductObject(response.data);
      } catch (error) {
        console.error("Error retrieving product object:", error);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4000/api/${productType}/${product}/reviews`
        );
        setReviews(response.data);
      } catch (error) {
        console.error("Error retrieving reviews:", error);
      }
    };

    fetchProductObject();
    fetchReviews();
  }, [productType, product, location]);

  const handleInputChange = (event) => {
    setUserReview({
      ...userReview,
      [event.target.name]: event.target.value
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    axios
      .post(`http://localhost:4000/api/${productType}/${product}/reviews`, userReview)
      .then((response) => {
        setReviews([...reviews, response.data]);
        setUserReview({ authorEmail: "", comment: "" });
      })
      .catch((error) => {
        console.error("Error adding review:", error);
      });
  };

  const ReviewDisplay = (props) => {
    return (
      <div className="review">
        <h2>{props.review.authorEmail}</h2>
        <p>{props.review.comment}</p>
      </div>
    );
  };

  return (
    <div>
      <div className="review-form">

        <h2>Write a Review</h2>
        {userContext ? (
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="authorEmail"
              placeholder="Your email"
              value={userReview.authorEmail}
              onChange={handleInputChange}
              required
            />
            <textarea
              name="comment"
              placeholder="Your Review"
              value={userReview.comment}
              onChange={handleInputChange}
              required
            ></textarea>
            <button type="submit">Submit Review</button>
          </form>
        ) : (
          <Link to="/signin">Sign in to add a review</Link>
        )}
      </div>
      {!reviews.length && (
        <div className="retrError">
          <h1>No reviews for this product</h1>
        </div>
      )}
      {reviews.length > 0 &&
        reviews.map((review) => <ReviewDisplay key={review.id} review={review} />)}
    </div>
  );
}

export default Reviews;
